from io import StringIO

test1 = """ULL
RRDDD
LURDL
UUUUD"""
actual = """



"""

text = actual

directions = { "U" : lambda p : p - 3 if p > 3 else p,
               "D" : lambda p : p + 3 if p < 7 else p,
               "L" : lambda p : p - 1 if ((p-1) % 3) > 0 else p,
               "R" : lambda p : p + 1 if ((p-1) % 3) < 2 else p }

pos = 5
with StringIO(text) as puzzle:
    for line in puzzle:
        for ch in line.strip():
            pos = directions[ch](pos)
        print(pos)
