from io import StringIO

test1 = """ULL
RRDDD
LURDL
UUUUD"""
actual = """



"""

text = actual

keypad = {                           (2,0) : "1",
                        (1,1) : "2", (2,1) : "3", (3,1) : "4",
           (0,2) : "5", (1,2) : "6", (2,2) : "7", (3,2) : "8", (4,2) : "9",
                        (1,3) : "A", (2,3) : "B", (3,3) : "C", 
                                     (2,4) : "D"
}

directions = { "U" : lambda p : (p[0],p[1]-1) if (p[0],p[1]-1) in keypad else p,
               "D" : lambda p : (p[0],p[1]+1) if (p[0],p[1]+1) in keypad else p,
               "L" : lambda p : (p[0]-1,p[1]) if (p[0]-1,p[1]) in keypad else p,
               "R" : lambda p : (p[0]+1,p[1]) if (p[0]+1,p[1]) in keypad else p }

pos = (0,2)
with StringIO(text) as puzzle:
    for line in puzzle:
        for ch in line.strip():
            pos = directions[ch](pos)
        print(pos, keypad[pos])
